    @Override
    public void playLevelMusic() {
        Music.INSTANCE.playTracks(
                new String[]{Assets.Music.HALLS_1, Assets.Music.HALLS_2},
                new float[]{1, 1},
                false);
    }
    @Override
    public Mob chooseMob(int wave) {
        Mob mob = new Rat();
        switch (wave){
            //scorpio = 110, Succubus = 80 + heal, Necromancer = 40 + skeleton = 25, TS = 5 hits, eye = 100 + 2 hitsav
            case 1:
                mob = new Succubus(); break;
            case 2:
                mob = new Necromancer(); break;
            case 3:
                mob = new TimelessSpirit(); break;
            case 4:
                mob = new TimelessSpirit(); break;
            case 5:
                mob = new SpectralNecromancer(); break;
            case 6:
                if (!bossSpawned) {
                    bossSpawned = true;
                    mob = new Acidic();
                } else mob = new Scorpio();
                break;
            case 7:
                mob = new Eye(); break;
            case 8:
                mob = new Wraith(); break;
            case 9:
                mob = new Wraith(); break;
            case 10:
                mob = new Necromancer();
                break;
            case 11:
                mob = new Acidic(); break;
            case 12:
                mob = new Wraith();
                break;
            case 13:
                mob = new TimelessSpirit();
                break;
            case 14:
                mob = new TimelessSpirit();
                break;
            case 15:
                mob = new SpectralNecromancer();
                break;
            case 16:
                mob = new Necromancer();
                break;
            case 17:
                mob = new Skeleton();
                break;
            case 18:
                mob = new SpectralNecromancer();
                break;
            case 19:
                mob = new SpectralNecromancer();
                break;
            case 20:
                mob = new Eye();
                break;
        }
        if (mode == WndModes.Modes.CHALLENGE){
            affectMob(mob);
        }
        return mob;
    }
    @Override
    public int mobsToDeploy(int wave) {
        switch (wave){
            case 1: return 3;
            case 2: return 5;
            case 3: return 15;
            case 4: return 18;
            case 5: return 7;
            case 6: return 5;
            case 7: return 5;
            case 8: return 25;
            case 9: return 25;
            case 10: return 15;
            case 11: return 7;
            case 12: return 40;
            case 13: return 30;
            case 14: return 35;
            case 15: return 19;
            case 16: return 21;
            case 17: return 50;
            case 18: return 25;
            case 19: return 26;
            case 20: return 20;
        } return 1;
    }

    @Override
    public void doStuffEndwave(int wave) {
        int goldAdd = 600;
        Dungeon.gold+=goldAdd;
        GLog.w(Messages.get(Arena.class, "goldaddendwave", goldAdd));
        super.doStuffEndwave(wave);
    }

    public void deployMobs(int wave) {
        if (wave % 5 == 3) deploymobs(wave, Direction.UP, 1);
        else if (wave % 5 == 4) deploymobs(wave, Direction.DOWN, 1);
        else if (wave == maxWaves) deploymobs(wave, Direction.RANDOM, 20);
            else deploymobs(wave, Direction.TOORIGHT, 1);
    }

    @Override
    public void addDestinations() {
        ArrayList<Integer> candidates = new ArrayList<>();
        for (int m = 0; m<WIDTH*HEIGHT;m++){
            if (passable[m] && !cellAdjacentToBorderCells(m) && distance(m, amuletCell)>15) candidates.add(m);
        }

        for (int i = 0;i < 10; i++) dropMany(Heap.Type.SKELETON, candidates,
                Generator.randomWeapon().upgrade(),
                Generator.randomArmor().upgrade()
        );
        for (int i = 0;i < 10; i++) dropMany(candidates,
                Generator.random(Generator.Category.POTION),
                Generator.random(Generator.Category.STONE),
                Generator.random(Generator.Category.WEAPON)
        );
        for (int i = 0; i < 5 ; i++){
            drop(new ScrollOfSkulls(), 16+ WIDTH*14);
        }
        drop(TowerInfo.getTowerSpawner(slot1), amuletCell+1);
        drop(TowerInfo.getTowerSpawner(slot2), amuletCell-1);
        drop(TowerInfo.getTowerSpawner(slot3), amuletCell+WIDTH);
        drop(TowerInfo.getTowerSpawner(slot4), amuletCell-WIDTH);
        for (int i = 0; i < 15; i++) createMapGuard(candidates, new Necromancer());
        SkeletonArmoredShielded skeleguard = new SkeletonArmoredShielded();
        skeleguard.pos = 15 + WIDTH*47;
        skeleguard.mapGuard = true;
        GameScene.add(skeleguard);
        drop(new ScrollOfHolyNova(), 15+ WIDTH*47);
        super.addDestinations();
    }

    @Override
    public String tilesTex() {
        return Assets.Environment.TILES_SOULDESERT;
    }
